            System.out.println("The number must be positive.");
            return;
        }

        // Prompt the user to enter the second positive integer
        System.out.print("Enter the second positive integer: ");
        number2 = scanner.nextInt();

        // Check if the second number is positive
        if (number2 <= 0) {
            System.out.println("The number must be positive.");
            return;
        }

        // Calculate the GCD
        int gcd = findGCD(number1, number2);

        // Display the result
        System.out.println("The greatest common divisor of " + number1 + " and " + number2 + " is: " + gcd);

        scanner.close();
    }

    // Method to find the GCD using Euclid's algorithm
    public static int findGCD(int a, int b) {
        while (b != 0) {
            int temp = b;
            b = a % b;
            a = temp;
        }
        return a;
    }
}
